import React, { Component } from "react";
import { Key } from "react-feather";
import axios from "axios";

class LogIn extends Component {
  constructor() {
    super();
    this.state = {
      newUsername: "",
      newPassword: "",
      existingUsername: "",
      existingPassword: "",
      errorMessage: "",
      logInErrorMessage: "",
      toUserPortal: false,
    };
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
      errorMessage: "",
      logInMessage: "",
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    //this.logInUser();
  };

  logInUser = () => {
    axios
      .post("/api/authorize", {

      })
  }

  render() {
    console.log(this.state.existingPassword);
    return (
      <div className="container-fluid log-in-container">
        <header className="text-center log-in-header">
          <Key className="mx-auto d-block key-icon" />
          <h2 className="mt-2 log-in-text">Log in</h2>
        </header>
        <div className="row justify-content-center">
          <div className="col-5 input-col">
            <form onSubmit={this.handleSubmit}>
              <div class="form-group">
                <label className="label-text" for="">
                  User Id
                </label>
                <input
                  type="input"
                  className="form-control username-input"
                  onChange={this.handleChange}
                  placeholder=""
                  name="existingUsername"
                />
              </div>
              <div class="form-group">
                <label className="label-text" for="exampleFormControlInput1">
                  Password
                </label>
                <input
                  type="input"
                  className="form-control username-input"
                  onChange={this.handleChange}
                  placeholder=""
                  name="existingPassword"
                />
              </div>
              <button
                type="button"
                className="btn btn-primary btn-lg mt-4 btn-block log-in-button"
              >
                Log in
              </button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default LogIn;
